#!/bin/bash

# SCRIPT D'IMPORTATION VIA BASE64 - M√âTHODE DE SECOURS
# √Ä ex√©cuter sur le serveur distant 37.59.121.40

echo "üîß IMPORTATION VIA BASE64 - M√âTHODE DE SECOURS"
echo "=============================================="
echo ""

DB_NAME="clubcreole_db"
DB_USER="postgres"

echo "√âtape 1: Installation PostgreSQL si n√©cessaire"
if ! command -v psql &> /dev/null; then
    echo "Installation de PostgreSQL..."
    sudo apt update
    sudo DEBIAN_FRONTEND=noninteractive apt install -y postgresql postgresql-contrib
else
    echo "‚úÖ PostgreSQL d√©j√† install√©"
fi

echo ""
echo "√âtape 2: D√©marrage service PostgreSQL"
sudo systemctl start postgresql
sudo systemctl enable postgresql

echo ""
echo "√âtape 3: Cr√©ation base de donn√©es"
if sudo -u postgres psql -lqt | cut -d \| -f 1 | grep -qw $DB_NAME; then
    echo "Suppression de l'ancienne base $DB_NAME..."
    sudo -u postgres dropdb $DB_NAME
fi

echo "Cr√©ation de la base $DB_NAME..."
sudo -u postgres createdb -O $DB_USER $DB_NAME

echo ""
echo "√âtape 4: Cr√©ation du fichier dump via base64"
echo "Collez le contenu base64 ci-dessous (Ctrl+D pour terminer):"

# Cr√©ation du fichier base64
mkdir -p /home/ubuntu/dumps
cd /home/ubuntu/dumps

cat > supabase_dump_20251028_164145.sql.b64 << 'BASE64_EOF'

BASE64_EOF

echo ""
echo "√âtape 5: D√©codage du fichier base64"
echo "D√©codage en cours..."
base64 -d supabase_dump_20251028_164145.sql.b64 > supabase_dump_20251028_164145.sql

if [ $? -eq 0 ]; then
    echo "‚úÖ Fichier SQL cr√©√© avec succ√®s"
    echo "üìè Taille du fichier: $(du -h supabase_dump_20251028_164145.sql | cut -f1)"
    
    echo ""
    echo "√âtape 6: Importation des donn√©es"
    echo "Importation en cours (peut prendre plusieurs minutes)..."
    
    sudo -u postgres psql -d $DB_NAME -f supabase_dump_20251028_164145.sql
    
    if [ $? -eq 0 ]; then
        echo ""
        echo "‚úÖ IMPORTATION R√âUSSIE !"
        echo ""
        echo "√âtape 7: V√©rification"
        echo "Tables cr√©√©es:"
        sudo -u postgres psql -d $DB_NAME -c '\dt' | head -10
        
        echo ""
        echo "Nombre d'enregistrements par table:"
        sudo -u postgres psql -d $DB_NAME -c "
        SELECT 
            schemaname,
            tablename,
            n_tup_ins as enregistrements
        FROM pg_stat_user_tables 
        WHERE n_tup_ins > 0
        ORDER BY n_tup_ins DESC
        LIMIT 10;"
        
        echo ""
        echo "üéØ BASE DE DONN√âES PR√äTE !"
        echo "Informations de connexion:"
        echo "- H√¥te: localhost"
        echo "- Base: $DB_NAME"
        echo "- Utilisateur: $DB_USER"
        echo "- Port: 5432"
        echo ""
        echo "Commande de connexion:"
        echo "sudo -u postgres psql -d $DB_NAME"
        
    else
        echo "‚ùå Erreur lors de l'importation"
        echo "V√©rifiez le contenu du fichier:"
        head -20 supabase_dump_20251028_164145.sql
    fi
else
    echo "‚ùå Erreur lors du d√©codage base64"
fi